let odds=[0,0],totalStake=0,renderBets=()=>{document.querySelector(".bets").innerHTML="";for(let e=0;e<odds.length;e++)document.querySelector(".bets").innerHTML+=`<div class="bet"><label for="bet-${e+1}">Bet ${e+1}</label><input type="number" id="bet-${e+1}" value=${odds[e]} class="bet-input" placeholder="Enter odds"><div class="result"><span>$ 0.00</span></div><div class="result"><span>$ 0.00</span></div></div>`;removeButtonController()},addBet=()=>{odds.push(0),renderBets()},removeBet=e=>{document.querySelector(".bets").children.length<=e||(odds.splice(e,1),renderBets(),calculateBets())},reset=()=>{odds=[0,0],renderBets()},calculateBets=()=>{totalStake},removeButtonController=()=>{document.querySelectorAll(".bet")},handleInputValues=e=>{console.log("kjflsj"),"-"===e.target.value[e.target.value.length-1]&&(console.log("da"),e.target.value=e.target.value.substring(0,e.target.value.length-1))};document.addEventListener("DOMContentLoaded",function(){document.getElementById("add-bet-button").addEventListener("click",addBet),document.getElementById("reset-button").addEventListener("click",reset),document.querySelectorAll("input").forEach(e=>e.addEventListener("input",handleInputValues)),renderBets()});